int fibonacci(int n) 
{ 
    // Base case 
    if (n == 0) 
        return 0; 
    // Recursive case 
    else if (n == 1) 
        return 1; 
    else 
        return fibonacci(n - 1) + fibonacci(n - 2); 
} 

// Driver program 
int main() 
{ 
    int n; 
    cout << "Enter the value of n: "; 
    cin >> n; 
    cout << "The nth Fibonacci number is: " << fibonacci(n); 
    return 0; 
}
